import React from 'react';
import './Products.css';

function Product({ product }) {
    const { title, subTitle, description, image, buttons, disclaimer, disclaimerLink } = product;

    return (
        <div className='product-container' style={{ backgroundImage: `url(${image})` }}>
            <h1>{title}</h1>
            { subTitle && <p>{subTitle}</p> }
            { description && <p>{description}</p> }
            <div>
                {
                    buttons.map(button => (
                        <a href={button.link}>{button.text}</a>
                    ))
                }
            </div>
            { disclaimer && <p>{disclaimer}</p> }
            { disclaimerLink && <a href={disclaimerLink.link}>{disclaimerLink.text}</a>}
        </div>
    );
}

export default Product;
