import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './mobileModal.css';
import { faXmark, faChevronLeft } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { navModalContent } from '../navModal/navModalContent';

function MobileModal({ isVisible, onClose }) {
    const [ isMenuActive, setIsMenuActive ] = useState(false);
    const [ activeMenu, setActiveMenu ] = useState();

    const openMenu = menu => {
        setIsMenuActive(true);
        setActiveMenu(menu);
    }

    const closeMenu = () => {
        setIsMenuActive(false);
        setActiveMenu(null);
    }

    return ReactDOM.createPortal(
        <div className={`mobile-modal ${isVisible ? 'visible' : ''}`}>
            <div id='mobile-close-container'>
                <button type="button" className={`mobile-menu-back mobile ${isMenuActive ? 'visible' : ''}`} onClick={closeMenu}><FontAwesomeIcon icon={faChevronLeft} style={{ color: "black" }} size="xl" /></button>
                { isMenuActive && <h2>{activeMenu}</h2>}
                <button type="button" className="modal-close mobile" onClick={onClose}><FontAwesomeIcon icon={faXmark} style={{ color: "black" }} size="lg" /></button>
            </div>
        {Object.keys(navModalContent).map(menu => (
            <button className="mobile-menu-button" onClick={() => openMenu(menu)}>{menu}</button>
        ))}
        </div>,
        document.getElementById('modal-root')
    );
}

export default MobileModal;
